        $("#estudiante").hide();
        $("#trabajador").hide();
        $("#autonomo").hide();
        $("#paro").show();
    }
    }

    const options = [
      {id:"Company", label: 'Your company', value:'Your company'},
      {id:"How",label: 'How did you find us?',  value: 'How did you find us?'},
