import React, {Fragment, useCallback, useState, useRef, Component} from "react";
import Dropdown from './Drop1';
import 'react-dropdown/style.css';
import {Card, ChoiceList, Button, Heading,EmptyState, Layout,Page, Stack, ActionList, Avatar, ContextualSaveBar, FormLayout,Frame, Loading,Modal, Navigation, SkeletonBodyText, SkeletonDisplayText, SkeletonPage, TextContainer, TextField, Toast, TopBar, Link} from "@shopify/polaris";
import { Redirect } from '@shopify/app-bridge/actions';
import {ArrowLeftMinor, NoteMajorMonotone, HomeMajorMonotone, OrdersMajorTwotone, MobileHamburgerMajorMonotone, ListMajorMonotone, BankMajorMonotone,TroubleshootMajorMonotone,SettingsMajorMonotone} from '@shopify/polaris-icons';
import AppProvider from './navbar.js';







class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      dias: ['Lunes','Miércoles']
    }
    this.cambioDia = this.cambioDia.bind(this);
  }
  render() {
    return (
      <div>
        <p><select multiple value={this.state.dias} onChange={this.cambioDia}>
        <option>Lunes</option>    
        <option>Martes</option>
        <option>Miércoles</option>
        <option>Jueves</option>
        <option>Viernes</option>
        <option>Sábado</option>
        <option>Domingo</option>
        </select></p>
        Día seleccionado:{this.state.dias.map((elemento)=>{
          return (<p>{elemento}</p>)
        }
        )}
      </div>
    );
  }

  cambioDia(e) {
    const opciones = e.target.options;
    const seleccionadas = [];
    for (let i = 0; i < opciones.length; i++) {
      if (opciones[i].selected) {
        seleccionadas.push(opciones[i].value);
      }      
    }    
    this.setState({
      dias: seleccionadas
    })
  }
}

export default App;


  
  const [selected, setSelected] = useState(['hidden']);

  const handleChange = useCallback((value) => setSelected(value), []);



<>

  <Card sectioned title=" Create attribute">
  <Stack distribution="leading">
  <Link url='/Index' >
  Home
  </Link>
           
  <Link url='/componenteslist' >
  Attributes
</Link>

<p>
  Attribute
</p>
</Stack>
            
        </Card>
        <Page>
  
  
       
  
  
  <FormLayout>
      <Card sectioned>
      
      <p>Use Template:</p>
      <Stack distribution="fillEvenly">
  
      <Dropdown id="drop1"/>
     
      <p>If you choose a template, the following 
        attribute fields will be filled 
        <br/>
        automatically. Or you can leave this 
        <br/>
        box blank and create your unique field 
        <br/>
        by filling in these boxes.</p>
      </Stack>
      <hr/>
      <br/>
      <Heading>Online store dashboard</Heading>    
      <br/>
      <hr/>
      <br/>
  
      <TextField
        label="Label*:"
        placeholder="Attribute Label"
      />
      <p>Attribute label</p>
      <br/>
  
      <TextField
        label="Code:"
      />
      <p>For internal use. Must be unique with 
        no spaces and length  30. Let 
        it empty for auto generation</p>
  
        <TextField
        label="Quantity"
        type="number"
      />
      <br/>
  
        <p>Type*:</p>
  
      <Dropdown id="drop2"/>
  
      <br/>
      <Heading>Attribute Configuration</Heading>    
      <br/>
        <hr/>
  
       
        <ChoiceList
      allowMultiple
      choices={[
        {
          label: 'Show on Cart page:',
          value: 'shipping',
          helpText:
            'Tick if you want to use this attribute on cart page.',
        },
        {
          label: 'Hide if filled:',
          value: 'confirmation',
          helpText:
            'Hide this attribute if it was filled before.',
        },
        {
          label: 'Show on Order grid:',
          value: 'shipping',
          helpText:
            'Tick if you want to use this attribute on order grid in app admin.',
        },
        {
          label: 'Show on Register page:',
          value: 'confirmation',
          helpText:
            'Tick if you want to use this attribute on register page.',
        },
        {
          label: 'Show on Customer grid',
          value: 'confirmation',
          helpText:
            'Tick if you want to use this attribute on customer grid in app admin',
        },
      ]}
      selected={selected}
      onChange={handleChange}
    />
      </Card>


  
      </FormLayout>
  
  </Page>
  
  </>
