      <hr/>
      <TextField
        label="Label*:"
        placeholder="Your Company"
      />
      </Card>
</Form>
</div>

<div id="How" style={{display: 'none'}}>
<Form  >
      <Card sectioned title="Attribute Properties">
      <hr/>
      <TextField
        label="Label*:"
        placeholder="How"
      />
      </Card>
</Form>
</div>

<select id="status" name="status" onChange={mostrar}>
        <option value="How">Estudiante</option>
        <option value="Company">Trabajador</option>
        <option value="autonomo">Aut√≥nomo</option>
        <option value="paro">En el paro</option>
     </select>

  <Dropdown
   onChange={this.handleChange}  
  options={options}
  placeholder="Choose an option" 
 selection 
/>
      
           


  

</>
    )

  }
}
